function calcIncl(){var e=document.getElementById("excl").value.replace("€ ",""),c=parseInt(document.getElementById("perc").value,10);document.getElementById("excl").value="€ "+e,document.getElementById("btw").value="€ "+(e/100*c).toFixed(2).replace(/\./g,".").replace(".00",""),document.getElementById("incl").value="€ "+(e/100*(100+c)).toFixed(2).replace(/\./g,".").replace(".00",""),lastFunction="incl"}function calcExcl(){var e=document.getElementById("incl").value.replace("€ ",""),c=parseInt(document.getElementById("perc").value,10);document.getElementById("incl").value="€ "+e,document.getElementById("btw").value="€ "+(e/(100+c)*c).toFixed(2).replace(/\./g,".").replace(".00",""),document.getElementById("excl").value="€ "+(e/(100+c)*100).toFixed(2).replace(/\./g,".").replace(".00",""),lastFunction="excl"}function percChange(){"incl"===lastFunction?calcIncl():calcExcl()}document.getElementById("excl").focus();var lastFunction="none";